import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import moment from 'moment';
import {useRouter} from 'next/router';
import Head from 'next/head';
import {markdown} from 'markdown';
import Link from 'next/link';

import Header from '../../templates/Header';
import Footer from '../../templates/Footer';
import HeaderContainer from '../../components/HeaderContainer';
import Container from '../../components/Container';
import Image from '../../components/Image';
import Clearfix from '../../components/Clearfix';

import {getMenuColour, getFullUrl, getData, getAllColours} from '../../helpers';

const Subtitle = styled.div`
  height: 200px;
  position: relative;
  background-color: ${props => props.colour};
  color: white;
  text-decoration: none;

  @media screen and (min-width: 768px) {
    height: 500px;
  }
`;

const Title = styled.h2`
  font-size: 30px;
  line-height: 1.5em;
  color: #fff;
  text-align: center;
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  transform: translateY(-50%);

  @media screen and (min-width: 768px) {
    font-size: 90px;
  }
`;

const ContentLeft = styled.div`
  padding: 20px;
  background-color: ${props => props.colour};
  color: white;
  line-height: 1.3em;
  margin-bottom: 60px;

  @media screen and (min-width: 991px) {
    float: left;
    width: calc(40% - 40px);
  }
`;

const ContentRight = styled.div`
  padding: 20px;
  background-color: ${props => props.colour};
  color: white;
  line-height: 1.3em;
  margin-bottom: 60px;

  @media screen and (min-width: 991px) {
    float: right;
    width: calc(60% - 40px);
  }

  li {
    list-style: inside;
    margin-bottom: 10px;
  }
`;

const StyledLink = styled.a`
  color: ${props => props.colour};
  display: inline-block;
  border: 3px solid ${props => props.colour};
  padding: 10px;
  border-radius: 5px;
  margin-top: 10px;
`;

const Article = styled.div`
  p {
    margin-bottom: 1em;
  }
`;

const Event = () => {
  const [loading, setLoading] = useState(true);
  const [title, setTitle] = useState('');
  const [image, setImage] = useState('');
  const [dateTime, setDateTime] = useState('');
  const [time, setTime] = useState('');
  const [article, setArticle] = useState('');
  const [colour, setColour] = useState('');
  const [colourHex, setColourHex] = useState('');
  const [colourHexLight, setColourHexLight] = useState('');
  const [pathname, setPathname] = useState('');
  const router = useRouter();

  const {
    query: {id}
  } = router;

  useEffect(() => {
    if (!id) return;
    setPathname(
      id
        .split(' ')
        .join('-')
        .split('%20')
        .join('-')
        .toLowerCase()
    );
  }, [id]);

  useEffect(() => {
    if (pathname.length === 0) return;

    getData(`data/${pathname}.json`).then(data => {
      const [colour, colourHex, colourHexLight] = getAllColours(
        getMenuColour(data)
      );
      setColour(colour);
      setColourHex(colourHex);
      setColourHexLight(colourHexLight);
      setDateTime(moment(data.dateTime).format('dddd DD MMM YYYY'));
      setTime(moment(data.dateTime).format('HH:mm'));
      setTitle(data.title);
      setImage(data.image);
      setArticle(data.article);
      setLoading(false);
    });
  }, [pathname]);

  if (!id) return null;

  if (loading) return <div />;

  return (
    <React.Fragment>
      <Head>
        <title key="title">{title} - Gateway Church, York</title>
      </Head>
      <Header
        colour={colour}
        colourHex={colourHex}
        colourHexLight={colourHexLight}
        Header={HeaderContainer}
      />
      <main>
        <Subtitle colour={colourHex}>
          <Image url={getFullUrl(image)} />
          <Title>{title}</Title>
        </Subtitle>
        <Container>
          <ContentLeft colour={colourHexLight}>
            <div>{dateTime}</div>
            <div>{time}</div>
          </ContentLeft>
          <ContentRight colour={colourHex}>
            {article && (
              <>
                <Article
                  dangerouslySetInnerHTML={{__html: markdown.toHTML(article)}}
                />
                <Link href="/events" passHref>
                  <StyledLink colour={colourHexLight}>
                    View a list of all events
                  </StyledLink>
                </Link>
              </>
            )}
          </ContentRight>
          <Clearfix />
        </Container>
      </main>
      <Footer />
    </React.Fragment>
  );
};

export default Event;
